import React,{Component, useState} from "react";
import QrReader from 'react-qr-reader'
import extractEmails from "./emailAddress";
class Email extends Component {
  state = {
    result: '',
    value : ''
  }
 
  handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
  handleError = err => {
    console.error(err)
  }


handleChange =event=>{
  
  this.setValue({value : event.target.value}
  )
}

render(){
return (
  <div className=" row  text-white bg-dark">
        <div className="col-6">
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '100%' }}
        />
        {this.state.result}
         </div>
  <div className="col-6">
<form>
  <div class="form-group">
    <label for="exampleInputEmail1"><h1>Email address</h1></label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" value={extractEmails(this.state.result)} onChange={this.handleChange}/>
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" />
  </div>
  <div class="form-group form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1" />
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
</div>
</div>
);
};
}

export default Email;
