import React, { Component } from 'react'
import QrReader from 'react-qr-reader'

class App extends Component {
  state = {
    result: 'No result'
  }

  handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
  handleError = err => {
    console.error(err)
  }
  getMobileNumber=(text)=>{
    const mobile =(text.match.replace(/\D/g, ''))


    return(
      <div>
      {text}
      <br></br>
      {(mobile)}
      </div>
    )
  }
  render() {
    return (
      <div>
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '50%' }}

        />

        <p>{this.getMobileNumber(this.state.result)}</p>
      </div>
    )
  }
}
export default App;
