import React,{useState} from 'react';
import './App.css'
var QRCode = require('qrcode.react');

const Generate = ()=>{
  const [value,setValue]=useState('');
  
  const handleChange = event=>{
    event.preventDefault();
    setValue(event.target.value)
  }

  const downloadQR = () => {
    const canvas = document.getElementById("123456");
    const pngUrl = canvas
      .toDataURL("image/png")
      .replace("image/png", "image/octet-stream");
    let downloadLink = document.createElement("a");
    downloadLink.href = pngUrl;
    downloadLink.download = "123456.png";
    document.body.appendChild(downloadLink);
    downloadLink.click();
    document.body.removeChild(downloadLink);
  };

  return(
    
    <div className="x">
        <div className="form-group">
      <form>
        <label className="text-white text-center my-0">
          <h1>Enter The Info</h1>
          <input className="form-control p-8 my-4 ml-0" id="input-lg" type="text" value={value} onChange={handleChange} />
        </label>
      </form>
      <br></br>
      <QRCode
        id="123456"
        value={value}
        size={290}
        level={"H"}
        includeMargin={true}
      />
      <br></br>
      <a onClick={downloadQR}type="button" className="btn btn-dark text-white btn-lg"> Download QR </a>
    </div> 
    </div>
  )
}
export default Generate;
